import React, { useState } from "react";
import { Button, FormGroup, FormControl } from "react-bootstrap";
import './App.css';


export default function App1(props) {
  const [text, setText] = useState("");
  const [password, setPassword] = useState("");

  function validateForm() {
    return text.length > 0 && password.length > 0;
  }

  function handleSubmit(event) {
    event.preventDefault();
  }

return (
<div className="Login">
 
      <form onSubmit={handleSubmit}>
          <FormGroup controlId="email" bssize="large">
          <label htmlFor="Username">User Name</label>
            <FormControl autoFocus type="text" placeholder="User Name..." value={text} onChange={e => setText(e.target.value)} />
          </FormGroup>

          <FormGroup controlId="password" bssize="large">
            <label htmlFor="Password">Password</label>
            <FormControl placeholder="Password" value={password} onChange={e => setPassword(e.target.value)} type="password" />
          </FormGroup>
        
          <Button block bssize="large"  disabled={!validateForm()}  type="submit">Login</Button>

      </form>

</div>
  );
}
