import React from "react";
import "./feed.css";
import axios from "axios";
class Feed extends React.Component {
  constructor() {
    super();
    this.state = {
      value: 5,
     ques:5
    };
    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }
  handleChange(event) {
    this.setState({ value: event.target.value, ques: event.target.value });

  }
  handleSubmit(event) {
    event.preventDefault();
    console.log(this.state.value);
    console.log(this.state.ques);
    // alert('Your favorite flavor is: ' + this.state.value);
    // let formData = new FormData();
    // formData.append('name', this.state.value)
    axios({
      method: "post",
      url: `http://localhost/backend/index.php`,
      headers: { "content-type": "application/json" },
      data: this.state
    })
      .then(result => console.log(result))
      .catch(error => this.setState({ error: error.message }));
  }
  render() {
    return (
      <div>
        <label className="label">A. TIME MANAGEMENT SKILLS</label>

        <div className="container">
          <form onSubmit={this.handleSubmit}>
            <p>
              1
              <label htmlFor="question" className="fa fa-long-arrow-right">
                Punctuality in the Class
              </label>
            </p>
            <input
              type="range"
              className="slider"
              id="customRange1"
              min="0"
              max="10"
              value={this.state.value}
              onChange={e => this.setState({value: e.target.value})}
            />

            <p>
              2
              <label htmlFor="question" className="fa fa-long-arrow-right">
                Regularity in taking Classes
              </label>
            </p>
            <input
              type="range"
              className="slider"
              id="customRange1"
              min="0"
              max="10"
              value={this.state.ques}
              onChange={e => this.setState({que: e.target.value})}
            />

            {/* <p>3<label htmlFor="question" className="fa fa-long-arrow-right">Student's attendance/presence in the class of teacher who is being evaluated</label></p>
                           <input type="range" className="slider" id="customRange1" min="0" max="10" value={this.state.value}  onChange={this.handleChange} />

                           <p>4<label htmlFor="question" className="fa fa-long-arrow-right">Completes syllabus of the course as per teaching plan</label></p>
                           <input type="range" className="slider" id="customRange1" min="0" max="10" value={this.state.value}  onChange={this.handleChange} />

                           <p>5<label htmlFor="question" className="fa fa-long-arrow-right">Scheduled organization of assignments, class, test, quizzes and seminars</label></p>
                           <input type="range" className="slider" id="customRange1" min="0" max="10" value={this.state.value}  onChange={this.handleChange} />

                           <p>6<label htmlFor="question" className="fa fa-long-arrow-right">Makes alternate arrangement of class in his/her absence</label></p>
                           <input type="range" className="slider" id="customRange1" min="0" max="10" value={this.state.value}  onChange={this.handleChange} /> */}
            <input type="submit" value="Submit" />
          </form>
        </div>

        <a href="/feed2">
          <button type="button" className="btn btn-success">
            Next
          </button>
        </a>
      </div>
    );
  }
}

export default Feed;
