import React from 'react'; // get the React object from the react module
import './feed.css';
import SliderPage from './slider';

class Feed2 extends React.Component {
  constructor(){
    super();
    this.state = {
      value:5
    }
    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);

  }
  handleChange(event) {
    this.setState({value: event.target.value});
    console.log(this.state.value)
  }
  handleSubmit(event) {
    // alert('Your favorite flavor is: ' + this.state.value);
    event.preventDefault();


  }
  render() {
    return(
        <div>
          <label className='label'>B. SUBJECT COMMAND & COMMUNICATION SKILLS</label>
            <div className='container'>
            <form onSubmit={this.handleSubmit}>
                       
                       <p>1<label for="question" className="fa fa-long-arrow-right">Focus on Syllabi</label></p>
                       <input type="range" className="slider" id="customRange1" min="0" max="10" value={this.state.value}  onChange={this.handleChange} />
                   
                       <p>2<label for="question" className="fa fa-long-arrow-right">Self-confidence</label></p>
                       <input type="range" className="slider" id="customRange1" min="0" max="10" value={this.state.value}  onChange={this.handleChange} />
                   
                       <p>3<label for="question" className="fa fa-long-arrow-right">Communication skills</label></p>
                       <input type="range" className="slider" id="customRange1" min="0" max="10" value={this.state.value}  onChange={this.handleChange} />
                  
                       <p>4<label for="question" className="fa fa-long-arrow-right">Completes syllabus of the course as per teaching plan</label></p>
                       <input type="range" className="slider" id="customRange1" min="0" max="10" value={this.state.value}  onChange={this.handleChange} />
               
                       <p>5<label for="question" className="fa fa-long-arrow-right">Scheduled organization of assignments, class, test, quizzes and seminars</label></p>
                       <input type="range" className="slider" id="customRange1" min="0" max="10" value={this.state.value}  onChange={this.handleChange} />
                    
                       <p>6<label for="question" className="fa fa-long-arrow-right">Makes alternate arrangement of class in his/her absence</label></p>
                       <input type="range" className="slider" id="customRange1" min="0" max="10" value={this.state.value}  onChange={this.handleChange} />
                   
                </form>
                        <p>7<i class="fa fa-long-arrow-right"></i> Focus on Syllabi<SliderPage />
                        8<i class="fa fa-long-arrow-right"></i> Self-confidence<SliderPage />
                        9<i class="fa fa-long-arrow-right"></i> Communication skills<SliderPage />
                        10<i class="fa fa-long-arrow-right"></i> Conducting classroom discussions<SliderPage />
                        11<i class="fa fa-long-arrow-right"></i> Teaching the subject matter<SliderPage />
                        12<i class="fa fa-long-arrow-right"></i> Skill of linking subject to life experience & creating intrest in the subject<SliderPage />
                        13<i class="fa fa-long-arrow-right"></i> Reference to latest developments in the field<SliderPage /></p>
            </div>
            <a href="/feed3" className="btn btn-dark btn-sm my-1"><button type="button" className='btn btn-success'>Next</button></a>
        </div>

    );  
  }
}

export default Feed2;
