import React from 'react'; // get the React object from the react module
import './feed.css';
import SliderPage from './slider';

class Feed3 extends React.Component {
  constructor(){
    super();
    this.state = {
      value:5
    }
    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);

  }
  handleChange(event) {
    this.setState({value: event.target.value});
    console.log(this.state.value)
  }
  handleSubmit(event) {
    // alert('Your favorite flavor is: ' + this.state.value);
    event.preventDefault();


  }
  render() {
    return(
        <div>
           <label className='label'>C. TEACHING SKILLS</label>
            <div className='container'>

            <form onSubmit={this.handleSubmit}>
                       
                       <p>1<label for="question" className="fa fa-long-arrow-right">Uses of teaching aids (OHP/Blackboard/PPT's)</label></p>
                       <input type="range" className="slider" id="customRange1" min="0" max="10" value={this.state.value}  onChange={this.handleChange} />
                   
                       <p>2<label for="question" className="fa fa-long-arrow-right">Blackboard/Whiteboard work in terms of legibility, visibility and structure</label></p>
                       <input type="range" className="slider" id="customRange1" min="0" max="10" value={this.state.value}  onChange={this.handleChange} />
                   
                       <p>3<label for="question" className="fa fa-long-arrow-right"></label></p>
                       <input type="range" className="slider" id="customRange1" min="0" max="10" value={this.state.value}  onChange={this.handleChange} />
                  
                       <p>4<label for="question" className="fa fa-long-arrow-right">Completes syllabus of the course as per teaching plan</label></p>
                       <input type="range" className="slider" id="customRange1" min="0" max="10" value={this.state.value}  onChange={this.handleChange} />
               
                       <p>5<label for="question" className="fa fa-long-arrow-right">Scheduled organization of assignments, class, test, quizzes and seminars</label></p>
                       <input type="range" className="slider" id="customRange1" min="0" max="10" value={this.state.value}  onChange={this.handleChange} />
                    
                       <p>6<label for="question" className="fa fa-long-arrow-right">Makes alternate arrangement of class in his/her absence</label></p>
                       <input type="range" className="slider" id="customRange1" min="0" max="10" value={this.state.value}  onChange={this.handleChange} />
                   
                </form>
                        <p>14<i class="fa fa-long-arrow-right"></i>Uses of teaching aids (OHP/Blackboard/PPT's)<SliderPage />
                        15<i class="fa fa-long-arrow-right"></i> Blackboard/Whiteboard work in terms of legibility, visibility and structure<SliderPage />
                        16<i class="fa fa-long-arrow-right"></i> Uses of innovation teaching methods<SliderPage />
                        17<i class="fa fa-long-arrow-right"></i> Shares the answers of class tests or sessional test questions<SliderPage />
                        18<i class="fa fa-long-arrow-right"></i> Shows the evaluated answer books of class tests to the students<SliderPage /></p>
            </div>
            {/* <button type="button" className='btn btn-success'>Submit</button> */}
            <a href="/" className="btn btn-dark btn-sm my-1"><button type="button" className='btn btn-success'>Submit</button></a>
        </div>

    );
  }
}

export default Feed3;
