import React from "react";
// import { ReactComponent } from "*.svg";

class SliderPage extends React.Component{
  constructor() {
    super();
    this.state = {
      value: 5,
     ques:5
    };
    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }
  handleChange(event) {
    this.setState({ value: event.target.value, ques: event.target.value });

  }

render(){
  return (
      <div className="SliderClass">
            <i className="fa fa-thumbs-o-down fa-xs"/>
            <input type="range" className="slider" id="customRange1" min="0" max="10" value={this.state.value} onChange={e => this.setState({value: e.target.value})} />
            <i className="fa fa-thumbs-o-up fa-xs"/>


    </div>
  );
  }
}
export default SliderPage;
